vec2 c;
#define rectangle(size, uv) (c = 1.-step(size, abs(uv)), c.x * c.y)
#define rotate45(uv) (vec2(uv.x+uv.y, uv.x-uv.y)/1.414213)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    vec2 uv = fract(9.* fragCoord / iResolution.y) - .5;
    uv = rotate45(uv);

	vec3 color = rectangle(vec2(.355), uv) * vec3(.73, .95, 1); 

    fragColor = vec4(color, 1.0);
}
