vec4 plas(vec2 v,float time)
{
  float c=.5+sin(v.x*110.)+cos(sin(time/4.+v.y)*20.);
  return vec4(sin(c*.92+cos(time/2.)),c*.015,cos(c*.91+time/2.4)*.25,1.);
}
void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
  vec2 uv=vec2(fragCoord.x/iResolution.x,fragCoord.y/iResolution.y);
  uv-=.5;
  uv/=vec2(iResolution.y/iResolution.x,1);

  vec2 origuv = uv; 
  float time = (iTime/20.);
  uv.y += (sin(uv.x*15.+time))/10.-(uv.x);
  origuv.x += time/110.;
  vec4 theNoise= texture(iChannel0,origuv);
  uv.y += (theNoise.r)/1115.;
  uv.x = abs(uv.x);
  uv.y = abs(uv.y);
  vec2 m;
  m.x = atan(uv.x / uv.y) / 3.14;
  m.y = 1. / length(uv) * .2;
  m.y += time + (iTime/110.);
  m.x += ((iTime/130.)*sin(m.y/100.));
  float d = m.y;
 float ratio = (sin((m.x*150.)+time*(-10.))/sin(m.y*5.))*500.;
//  out_color = theNoise;
//out_color = vec4(ratio);
// out_color = f + t;
  //float f = texture( iChannel0, d ).r * 100.;
  m.x += sin( iTime ) * .001;
  m.y += iTime * .0011;

  vec4 t = plas( m * 3.14, iTime ) / d;
  t = clamp( t, 0.0, 1.0 );
  vec4 out_color=vec4(1.);
 //out_color = f + t;
 out_color=(t*ratio);
 fragColor=vec4(out_color);
}
