//simple fading checker board
// checker board : https://www.shadertoy.com/view/tdKXRV
// kind of random value generator : https://stackoverflow.com/questions/4200224/random-noise-functions-for-glsl





#define N 64.0

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 floored_uv = floor( ( (fragCoord.xy)/iResolution.x) * N);


    vec2 color = mod(vec2(floored_uv.x+floored_uv.y) ,vec2(2.0)); // x+y even:black  odd:white



    if (color.x > 0.000001 && floored_uv.x < N/1.75) {

        color = vec2( step(0.4,floored_uv.x * rand(floored_uv) * 0.035  ) );

       }



    // Output to screen
    fragColor = vec4(color.xxx,1);
}
