float sdLine( in vec3 p, in vec3 a, in vec3 b )
{
	vec3 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

vec3 line( in vec3 buf, in vec3 a, in vec3 b, in vec3 p, in vec2 w, in vec4 col )
{
   float f = sdLine( p, a, b );
   float g = fwidth(f)*w.y;
   return mix( buf, col.xyz, col.w*(1.0-smoothstep(w.x-g, w.x+g, f)) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0*fragCoord.xy-iResolution.xy)/min(iResolution.y,iResolution.x);

	// background color
	vec3 col = vec3(0.0);

	// line
    vec3 a = vec3(-1.0, -0.3, 1.0);
	vec3 b = vec3(1.0, 0.3, 1.0);
	col = line(col, a, b, vec3(uv,1.0), vec2(0.007,1.0), vec4(0.7,0.0,0.0,1.0));

	fragColor = vec4( col,1.0 );
}
