#define SAMPLE_AMOUNT 20

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord/iResolution.xy;

    float strength = (distance(uv, vec2(0.5)) * distance(uv, vec2(0.5))) * -abs(sin(iTime) * 0.2);

    vec2 dir = normalize(uv - vec2(0.5, 0.5)) * strength;

	vec3 finalCol = vec3(0.0);

    for(int i = 0; i < SAMPLE_AMOUNT; ++i)
    {
        finalCol += texture( iChannel0, uv + dir * (float(i) / float(SAMPLE_AMOUNT) ) ).rgb / float(SAMPLE_AMOUNT);
    }

    fragColor = vec4(finalCol, 1.0);
}
