void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uvCircle = fragCoord/iResolution.xy;
    vec2 uv2 = fragCoord/iResolution.xy;
    vec2 background = fragCoord/iResolution.xy;

    uvCircle-=(iMouse.xy/iResolution.xy);

    uvCircle.x*=iResolution.x/iResolution.y;
    background.x*=iResolution.x/iResolution.y;

    float circle = smoothstep(0.0,1.0,length(uvCircle*4.0));

	vec2 d =vec2(dFdx(circle*4.0),dFdy(circle*4.0));


    vec3 colorOut = texture(iChannel0,background-d).xyz;
    // Output to screen
    fragColor = vec4(colorOut,1.0);
}
