/*
 * fork from https://www.shadertoy.com/view/3sdXRH by lennyjpg
 * replaced the colors with iq color pallete
 */

# define PI 3.141592653589793
float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float r(vec2 uv, int f ){
    float k = 2.;
    float t = floor(iTime*.6);
    return rand( floor( uv * pow(k, float(f))));
}

vec3 getColor(float t){ // 0.8, 0.5, 0.4 0.2, 0.4, 0.2	2.0, 1.0, 1.0	0.00, 0.25, 0.25
	vec3 a = vec3(0.8, 0.5, 0.4);
	vec3 b = vec3(0.2, 0.4, 0.2);
    vec3 c = vec3(2.0, 1.0, 1.0);
    float phase = (2.0 * cos(iTime) + 1.0) / 2.0; // added a little shift
    vec3 d = vec3(0.0, 0.25 * phase, 0.9 * phase);
    return a + b*cos( 6.28318*(c*t+d) );

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.y;
    vec2 u = uv*2.0;
    u += iMouse.xy / iResolution.xy * 10.0;
    u.y+=iTime*0.5;

    if(iMouse.z > 0.0){
     float angle = 0.0;
     float rad = rand(uv*2.5)*0.9;
      u.x += sin(angle)*rad;
  	  u.y += cos(angle)*rad;
    }

    float s  = r(u, 3);
    float m = r(u, 2);
    float l   = r(u, 1);
    float xl = r(u, 0);
    float f1 = round( r(u + 33., 1));
    float f2 = round( r(u + 10., 2));
    float f3 = round( r(u + 64., 0));
    float g = mix(s, m, f2);
    g = mix(g, l, f1);
    g = mix(g, xl, f3);
    vec3 col = getColor(8.*g);
    fragColor = vec4(col,1.);
}
