#define PI 3.1415

void rz(inout vec2 p, float a) {
    vec2 q = p;
    p.x = q.x * cos(a) - q.y* sin(a);
    p.y = q.x * sin(a) + q.y * cos(a);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    uv *= 0.5;

    vec2 deform,st,ru;
    float disTest,ClampTest,d,dd,dsq,ddsq,dis,col,r,sharp,shape,shape2,shape3,lineLength;
    vec4 clrOut = vec4(0.00,0.0,0.,0.);
    float a = atan(uv.y,uv.x);
    float Sharp = .5; 
    float t = -iTime * 0.1;

    deform = vec2(cos(t*5.), sin(t*5.))*0.01;
    d = length(uv);
    dd = length(uv+deform);

    float oder = 8.;
    dsq = pow( pow(uv.x,oder)+pow(uv.y,oder),1./(oder) );
    ddsq = pow( pow(uv.x+deform.x,oder)+pow(uv.y+deform.y,oder),1./(oder) );

    vec2 uvr = uv;
    float rd;
    float rdsq;
    rz(uvr,t*.75);
    //rz(uvr,t*.5*.75*sin(d*.5));
    rd = length(uvr);
    rdsq = pow( pow(uvr.x,oder)+pow(uvr.y,oder),1./(oder) );

    r = .05;

    shape = ( abs( sin(rdsq*30.)) - 
             0.7 * (1.+0.3*sin(a*8.+2.*PI*t)) * 
             (1.25+ .25*sin(t*10.)) * 
             (1.+0.1*sin(a*16.-4.*PI*t)) );

    shape2 = ( abs( sin(rdsq*30.)) - 
             0.7 * (1.+0.3*sin(a*8.+2.*PI*t)) * 
             (1.25+ .25*sin(t*10.)) * 
             (1.+0.1*sin(a*16.-4.*PI*t*.75*sin(d*.25))) );

    shape3 = ( abs( sin(rdsq*30.)) - 
             0.7 * (1.+0.3*sin(a*8.+200.*PI*(1.2*sin(t*2.*PI/10.))*sin(d*.25))) * 
             (1.25+ .25*sin( (1.2*sin(t*2.*PI/10.)) *400.*.75*sin(d*.1))) * 
             (1.+0.1*sin(a*16.-4.*PI*t)) );



    st = vec2(d*2., shape3);
    lineLength = .25+.45*sin(t*5.);
    lineLength += (1.-abs(sin(t*4.*5.)))*.2;
    dis = length(st - vec2(clamp(st.x, -lineLength, lineLength), 0.));

    col += smoothstep(Sharp, -Sharp, dis);

    clrOut += col * vec4(0.0,1.0,0.0,1.);
    clrOut = mix(clrOut, vec4(1.,.0,.0,.0)*col, d*3.);

    fragColor = clrOut;
}
