#define DEBUG 0

float clamp01(float v)
{
    return clamp(v, 0.0, 1.0);
}

float distortionHeight(vec2 uv) 
{
	float l = length(uv);
    float value = sin(l * 30.0 - iTime * 5.0) * 0.5 + 0.5;
    float value2 = sin(l * 150.0 - iTime * 30.0) * 0.5 + 0.5;
    value = pow(value, 1.5);
    value2 = pow(value2, 10.0);

    float attenuation = max(0.0, 1.0 - l * 0.75);
    return (value + (1.0 - value) * value2 * 0.02) * attenuation;
}

vec3 distortionNormal(vec2 uv, float strength)
{
    vec3 offset = vec3(1.0 / iResolution.xy, 0.0);
 	float p = distortionHeight(uv);
    float h1 = distortionHeight(uv + offset.xz);
    float v1 = distortionHeight(uv + offset.zy);

    vec2 delta = p - vec2(h1, v1);
    return vec3(delta * strength + 0.5, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy;
    vec2 uv = fragCoord / res;
    vec2 mouse = iMouse.xy / res;

    vec3 normal = distortionNormal(uv - mouse, 5.0);

    #if DEBUG == 1
       	fragColor = vec4(normal,1.0); 
    	return;
    #endif

	vec3 col = texture(iChannel0, uv + normal.xy * 0.1).rgb;

    fragColor = vec4(col,1.0);
}
