vec4 N14(float t) {
    return fract(sin(t*vec4(123., 1024., 3456., 9564.))*vec4(6547., 345., 7799., 1564.));
}

vec4 NN14(float t) {
    return fract(sin(t*vec4(1243., 10224., 13456., 69564.))*vec4(61547., 4345., 27799., 15564.));
}

float N11(float p) {
    float a = fract(p*345.65);
	a += dot(a, a+34.45);
    return fract(a);
}

float N21 (vec2 p) {
    return fract(sin(p.x*122.+p.y*52453.)*244156.); // returns a single randomish number based on a coord input
}

float RoundedBox( in vec2 p, in vec2 b, in float r) 
{
    vec2 q = abs(p) - b;
    float d = min(max(q.x,q.y),0.0) + length(max(q,0.0))-r;
    d *= 9.;
    return d;
}

vec3 superpal (in float p, in float n) {
	float pal = abs(floor(mod(p,35.)));
	float sel = abs(floor(n));
    return texture(iChannel0,vec2(sel/iResolution.x,pal/iResolution.y),0.0).rgb;
}

vec3 getTriad (in vec3 c1, in float n) {
	float r = c1.r;
 	float g = c1.g;
 	float b = c1.b;

	float sel = abs(floor(mod(n,3.)));
    if (sel==0.) return vec3(r,g,b);
    if (sel==1.) return vec3(g,b,r);
    if (sel==2.) return vec3(b,r,g);
    if (sel==3.) return vec3(b,g,r);
    if (sel==4.) return vec3(g,r,b);
    if (sel==5.) return vec3(r,b,g);
    return vec3(0.,0.,0.);    
}

vec3 hsv2rgb(vec3 c){  // taken from https://www.shadertoy.com/view/XdyXz3
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

