vec3 wiper (in vec2 uv) {
    // two active functions running, a and b.
    // start with a.  wipe to b.  change a, wipe to a, change b.  repeat.
    float pi = 3.141592653;
    float ti = (iTime*.33);
    // Put a thing in here to change the seed each cycle
    float pi1 = N11(mod(floor(ti / (pi))+pi*4.,104.)*4.5)*16.23;
    float pi1a = N11(mod(floor(ti / (pi))+pi*4.,104.)*4.5)*21.23;
    float pi2 = N11(mod(floor((ti-(pi*.5)) / (pi)),93.)*12.1)*39.47;
    float pi2a = N11(mod(floor((ti-(pi*.5)) / (pi)),93.)*12.1)*12.47;

    vec3 sca = getFunc(uv,pi2,pi2a);
    vec3 scb = getFunc(uv,pi1,pi1a);

    float size = (cos(ti*2.)*.5+.5)*5.2;

    float mask = clamp(smoothstep(size,size+0.02,length(uv)),0.,1.);

    vec3 ret = sca*(1.-mask);
    ret += scb*mask;

    return vec3(ret);
}
