void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color = vec3(0.0);

    vec2 translate = vec2(-0.5, -0.5);
    uv += translate;

    for(int i = 0; i < 20; ++i)
    {
    	float r = 0.3;
        float rd = radians(360.0 / 20.0) * float(i) + tan(iTime) * sin(iTime);

		float freq  = texelFetch( iChannel0, ivec2(uv.x,0), 0 ).x;
        color += (0.007 * (freq * 1.5)) / length(uv + vec2(r * cos(rd), r * sin(rd)));
    }

    fragColor = vec4(color, 1.0);
}
