void mainImage( out vec4 f, in vec2 g )
{
    f = pow(texture(iChannel0, g / iResolution.xy),vec4(2.0/3.2));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
//    fragColor = texture(iChannel0, (floor( (fragCoord*.98 + iResolution.xy*.01 + (fragCoord-iResolution.xy/2.).yx*vec2(-.03,.03)) )+.5) / iResolution.xy);
    fragColor = texture(iChannel0, (fragCoord*1.08 + iResolution.xy*.02 + (fragCoord-iResolution.xy/3.).yx*vec2(-.04,.04)) / iResolution.xy);

    float t = iTime*.6;

    vec4 col = vec4(sin(t*vec3(23,21,27))*.6+.6,2);
    float idx = .0+2.0*smoothstep( 7., 30., length( fragCoord - sin(vec2(21,23)*t)*70. - iResolution.xy/3. ) );
    fragColor = mix( col, fragColor, idx );
}
