void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 3.*(fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 c = 2.*iMouse.xy/iResolution.xy;

    const vec3 yelow = vec3(240., 230., 140.)/256.;
    const vec3 pink = vec3(255., 168., 175.)/256.;
    const vec3 blue = vec3(0., 166., 147.)/256.;

    vec2 z = uv;
    float iter = 0.;

    for(float i = 0.; i < 100.; i++){
        z = vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y) + c;

        if (length(z) > 16.) break;

        iter++;
    }

	vec3 col = vec3(iter/100.) * yelow;



    // Output to screen
    fragColor = vec4(col,1.0);
}
