void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 offset = vec2(iTime*0.05,sin(iTime*0.05));

	vec3 col = texture(iChannel0,uv+offset).rgb;

    fragColor = vec4(col,1.0);
}

vec3 hash33(vec3 p3)
{
	vec3 p = fract(p3 * vec3(.1031,.11369,.13787));
    p += dot(p, p.yxz+19.19);
    return -1.0 + 2.0 * fract(vec3((p.x + p.y)*p.z, (p.x+p.z)*p.y, (p.y+p.z)*p.x));
}

float worley(vec3 p, float scale){

    vec3 id = floor(p);
    vec3 fd = fract(p);

    float n = 0.;

    float minimalDist = 1.;


    for(float x = -2.; x <=2.; x++){
        for(float y = -2.; y <=2.; y++){
            for(float z = -2.; z <=2.; z++){

                vec3 coord = vec3(x,y,z);
                vec3 rId = hash33(mod(abs(id+coord),scale))*0.33;

                vec3 r = coord + rId - fd; 

                float d = dot(r,r);

                if(d < minimalDist){
                    minimalDist = d;
                }

            }//z
        }//y
    }//x

  return minimalDist;
}


float fbm(vec3 p,float scale){
  float G = exp(-0.3);
  float amp = 1.;
  float freq = 1.;
  float n = 0.;

  for(int i = 0; i <5; i++){
    n+= worley(p*freq,scale*freq)*amp;
    freq*=2.;
    amp*=G;
  }

  return n*n;  
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float scale = 2.;
    uv*=scale;

    vec3 p = vec3(uv,iMouse.x/iResolution.x*5. + iTime*0.1);

    float n = fbm(p,scale);

    vec3 col = vec3(1.-n);

    fragColor = vec4(col,1.0);
}
