#define SPLIT_POSITION 0.0

float drawLight(vec2 coord, vec2 pos, float radius, float intensity)
{
    return max(0.0, (radius - length(coord - pos)) * intensity);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse = (iMouse.xy-0.5*iResolution.xy)/iResolution.y;
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.0);

    float torch = drawLight(uv, vec2(0,0), 0.3, 2.0);
    float mouseLight = drawLight(uv, mouse, 0.4, 2.0);

    if (uv.x > SPLIT_POSITION)
    {
   		col = max(col, torch);
	    col = max(col, mouseLight);
    }else{
    	col += torch;
		col += mouseLight;
        col = min(vec3(1.0), col);
    }

    fragColor = texture(iChannel0, fragCoord / iResolution.xy) * vec4(col,1.0);
}
