void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.* fragCoord/iResolution.xy - 1.;

    float sq = iTime + sqrt(uv.x*uv.x+uv.y*uv.y);

    float col1 = sin(sq);
    float col2 = sin(sq+1.);
    float col3 = sin(sq+2.);

    // Output to screen
    fragColor = vec4(col1,col2,col3,1.0);
}
