void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 20.0 * (fragCoord - iResolution.xy / 2.0) / min(iResolution.y, iResolution.x);

    float lar;

    for (int i=0; i <15; i++)
    {
     lar = length(vec2(uv.x, uv.y));

        uv.x = uv.x - cos(uv.y + sin(lar)) + cos(iTime / 9.0);
        uv.y = uv.y - cos(uv.x + cos(lar)) + sin(iTime / 12.0);
    }





    // Normal Mode
    fragColor = vec4(cos(lar * 2.0), cos(lar * 3.0), cos(lar * 1.0), 1.0);

    //Darck mode
    //fragColor = vec4(cos(lar * 3.0), cos(lar * 1.0), cos(lar * 1.0), 1.0);


}
