#define t iTime
#define Rt mat2(cos(.5*t+vec4(0,33,11,0)))
#define _f(p)(1./6.)*sin(cos(t*fract(.14*t))\
+12.566*pow(sqrt(sqrt(fract(.3*t)+.5-dot((1./13.)*p,p))),7.))
float f(vec3 p){
vec3 b=abs(p)-vec3(2);
return max(max(b.x,max(b.y,b.z)),max(_f(p),_f((mod(p*2.,2.)-1.))));
}
void mainImage(out vec4 O,in vec2 U){
vec2 R=iResolution.xy,e=vec2(.001,0);
U=(U-.5*R)/R.y;O.xyz=.7*vec3(0.1,Rt*sin(floor(20.*fract(sin(2.*t)*2.*U.y+t)+30.*U.x)));
vec3 p,o=vec3(0,0,-7),L=normalize(vec3(5.*sin(t),5.*cos(t),1));
for(int i=32;i>0;--i){p=o;p.xy*=Rt;p.yz*=Rt;o+=vec3(U,1)*f(p);}
if(o.z<1.){vec3 N=normalize(f(p)-vec3(f(p-e.xyy),f(p-e.yxy),f(p-e.yyx)));
O.xyz=max(sin(6.*t+fract(.1*o.z))*vec3(.9,.4,.2)*exp2(.5*(2.-dot(p+L,p+L))),.5*vec3(.2,.5,.6)*max(0.,dot(N,p-L)));}
}

vec2 mSound(float tm)
{
    float p,s0,s1,a,b,t=mod(tm,1.),e=mod(tm,11.)*.5+.5;
    if(tm<15.5){
        p=floor(fract(.5*t)*5.)/3.;
        s0=sin(tm)-trunc(floor(2.*p)/2.);
        s0=atan(1.-p,fract(4.*tm)*p);
        a=e*cos(200.*s0*t)+sin(300.*s1*t);
        b=sin(100.*exp2(log2(4.+t)/(sin(tm)*(2.5+t)+a)))*exp(-8.*(.05+fract(8.*tm)));
    }else{
        p=floor(fract(2.5*t)*float[](5.,3.,8.)[int(.12*tm)%3])/3.;
        s1=float[](1.,16.,1.,16.)[int(tm)%2];
        s0=18.-trunc(floor(s1*p)/3.);
        s1=atan(1.-p,p*fract(4.*tm));
        a=e*cos(200.*s1*t)+sin(300.*s0*t);
        b=sin(100.*exp2(log2(4.*t)/((2.05+t)+a)))*exp(-8.*(.025+fract(8.*tm)));
    }
   return vec2(.35)*(min(.5*a-.1,.5)+min(.1+b,.5));
}
vec2 mainSound( in int samp,float time){return mSound(time);}
