// Fork of "XOR what are you doing?" by python273. https://shadertoy.com/view/4ldyW8
// 2018-12-30 11:23:19

vec3 hsv2rgb(vec3 c) {
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float temp = float(
        int(fragCoord.x + iTime * 80.0)
        ^  // try & |
        int(fragCoord.y + iTime * 80.0)
    );

    float mult=1. //1....10000
       +10.*(texelFetch( iChannel0, ivec2(49,2),0 ).x) //keyboard 1,2,3
      +100.*(texelFetch( iChannel0, ivec2(50,2),0 ).x)
     +1000.*(texelFetch( iChannel0, ivec2(51,2),0 ).x)
     ;

    vec2 adjust = vec2(
            (4.+iMouse.x)/1000.*mult,
           	 1.+iMouse.y/1000.*mult
       		);

    if (iMouse.xy==vec2(0)) //startup
    {
        adjust.r=3.;
        adjust.g=9999.;
    }

    fragColor = vec4(hsv2rgb(vec3(
        mod(temp,adjust.r*sin(temp)),
                                  1.0,
        mod(temp,adjust.g*cos(temp))
    )), 1.0);
}
