void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.y;

    st.x *= iResolution.x/iResolution.y;

    float colorStep=.25*sqrt(sin(iTime)+1.);
    vec3 rColor=vec3(1.,0.,0.);
    vec3 gColor=vec3(0.,1.,0.);
    vec3 bColor=vec3(0.,0.,1.);
    vec3 yColor=vec3(1.,1.,0.);
    float r=step(st.y,colorStep);
    float g=step(st.y-colorStep,colorStep)-r;
    float b=step(st.y-2.*colorStep,colorStep)-g-r;
    float y=step(st.y-3.*colorStep,colorStep)-r-g-b;
    vec3 color =vec3(r,g,b);

    // Output to screen
    fragColor = vec4(color,1.0);
}
