void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    // compenate aspect/ratio
    uv.x *= iResolution.x/iResolution.y;

    // define a circle position and radius
    vec3 cercle = vec3(0.0,0.0,0.4); // cercle centrer x,y, and radius z

    cercle.z += sin(iTime*2.0)/30.0;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float d = distance(cercle.xy, uv);
	vec3 col = (d<= cercle.z?vec3(d, 0.5, d/2.0):vec3(0.0));

    // Output to screen
    //fragColor = vec4(col,1.0);

    fragColor = vec4(col,1.0);
}
