vec3 red = vec3(225./255., 95./255., 60./255.);
vec3 white = vec3(1.,1.,1.);

vec3 circle(vec2 center,vec2 uv,float rad)
{
    float ratio = iResolution.x / iResolution.y;

    float x = sin(iTime * 1.5 ) ;

    uv.x *= ratio;

    float  value = distance(uv,vec2(.5 * ratio, .5))  * distance(uv,vec2(2., 1.1) * x);
    value = step(value,rad);

    vec3 color = mix(white,red,value);

    return color;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv  = fragCoord.xy / iResolution.xy;

    vec3 color = circle(vec2(.5,.5),uv,.05);
	fragColor = vec4(color,1.);


}
