void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //this is rotation pivot
    //float pivot = 0.5f; --> The value .f failed to mobile device on Apple (GLSL ES)
    float pivot = 0.5;
	vec2 expandUV = uv - pivot;

	float uR = dot(expandUV, vec2(cos(iTime), -sin(iTime)));
    float vR = dot(expandUV, vec2(sin(iTime), cos(iTime)));

    vec3 col = texture(iChannel0, vec2(uR, vR)).xyz;
    // Output to screen
    fragColor = vec4(col,1.0);
}
