float nsin(float x)
{
    return .5+.5*sin(x);
}

highp float rand(vec2 co)  //from stackoverflow
{
    highp float a = 12.9898;
    highp float b = 78.233;
    highp float c = 43758.5453;
    highp float dt= dot(co.xy ,vec2(a,b));
    highp float sn= mod(dt,3.14);
    return fract(sin(sn) * c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p=(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 curve_p = vec2(0,0);
    float dtemp = 1000.0;
    float d = 10001.0;
    float d2 = 10002.0;
    float d3 = 10003.0;
    float di = 0.;
    float threshold = 0.000000005;//0000006;
    float r = 0.;
    float g = 0.;
    float b = 0.;
    float black_threshold=0.;//5;// -.5*nsin(iTime*.6);

    for(int i = 0;i<3;i++)
    {
        di = float(i)*14.3;
        curve_p.x = -.1*sin(.6*iTime)+.02*float(di)*sin(float(di)+.4*iTime) +.03*float(di)*sin(4.0*di+.75*iTime) + .03*sin(iTime*.1)*float(di)*sin(7.0*di+.8*iTime);
        curve_p.y = -.1*cos(.6*iTime)+.02*float(di)*cos(float(di)+.4*iTime) +.03*float(di)*cos(4.0*di+.75*iTime) + .03*sin(iTime*.1)*float(di)*cos(7.0*di+.8*iTime);
        dtemp = pow(length(2.3*float(i)*p-curve_p),2.);


        d= dot(normalize(p),normalize(curve_p))+3.*nsin(.5*iTime+d*d);
        if(d<black_threshold) d=0.;   
        r = 28.*rand(vec2(float(i),float(i))); // +nsin(iTime*.8);
        g = g+.04*d*dtemp;
        b = .004*dtemp*dtemp-d - 3.5*mod(log(.6*dtemp*dtemp-3.*d),.60); //+.001*rand(vec2(34.3,floor(dtemp*32.))); //b + .05*dtemp-.05*d;  
    }
    vec3 col = vec3(r, g, b);
    float dn = col.x + col.y + col.z;
    col /= dn;
    // Output to screen
    fragColor = vec4(col,1.0);
}
