void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (12.0+sin(iTime)*4.0) * (fragCoord - iResolution.xy / (2.0)) / min(iResolution.y, iResolution.x);

    float lar;

    for (float i=0.0; i < 6.0; i++)
    {
        lar = length(vec2(uv.x, uv.y));
		float uvx1= uv.x;
        uv.x = - uv.y + sin(uv.x)*(uv.x);
        uv.y = - uvx1 + sin(uv.y)*(uv.y);
    }
    // Output to screen
    fragColor = vec4(cos(lar * 2.0), cos(lar * 3.0), cos(lar * 1.0), 1.0);
}
