void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 1.015 + 0.1*cos(iTime+uv.xyx+vec3(0,2,4)/200.0);
    float balanco = sin((iTime) + (uv.y*0.5))/50.0;

    // Output to screen

    if(uv.y*(col.y * col.x)  <= 0.165 && uv.x <= 0.165 + balanco + 5.0)
        fragColor = vec4 (0.6,0,0.8,0);
    else if(uv.y*(col.y * col.x ) <= 0.33 && uv.x <= 0.33 + balanco + 5.0)
        fragColor = vec4 (0.3,0.1,0.9,0);
    else if(uv.y*(col.y * col.x ) <= 0.485 && uv.x <= 0.485 + balanco + 5.0)
        fragColor = vec4 (0.5,1,0.6,0);
    else if(uv.y*(col.y * col.x) <= 0.65 && uv.x <= 0.65 + balanco + 5.0)
        fragColor = vec4 (0.9,0.8,0.1,0);
    else if(uv.y*(col.y * col.x ) <= 0.83 && uv.x <= 0.83 + balanco + 5.0)
        fragColor = vec4 (0.9,0.6,0.3,0);
    else if(uv.y*(col.y * col.x) <= 1.0 && uv.x <= 1.0 + balanco + 5.0)
        fragColor = vec4 (0.8,0,0.1,0);
    else
        fragColor = vec4 (0,0,0,0);




}
