mat2 r(float f){
    float c=cos(f),s=sin(f);
    return mat2(c,-s,s,c);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy -.5* iResolution.xy) / iResolution.y;
    vec2 uuv = uv*.5;
    uv*=r(atan(uv.x,uv.y)*2.5)*r(sign(cos(length(sin(uv*r(iTime)*10.)*3.)))*iTime);
    for(float i=0.;i<=4.;i++){
        uv = abs(uv)-.5;
        uv *=r(sign(sin(length(uv*5.)))*length(uuv));
    }
    float d = smoothstep(-1.,1.9-length(uv-uv)*4.,tan(-iTime+cos(iTime)*5.+10.*(uv.y+uv.x))/tan(iTime-sin(-iTime)*5.+10.*(uv.y-uv.x))  );
    vec3 col = vec3(d);
    col = mix(vec3(.1/length(uv),.1,.1/length(uv)),vec3(.1,.2+.1/length(uv),length(uuv)+length(uv)),col);
    fragColor = vec4(col,1.0);
}
