// White circle on black background
// From https://www.youtube.com/watch?v=GgGBR4z8C9o

float Circle(vec2 uv, float r, float blur) {
    // uv is the (x,y) position

    // d is the distance from the center to the given (x,y) uv coordinate
    // this affects the radius of the circle, larger d is a smaller circle    
    float d = length(uv);

	// c is the color of the pixels in the image, per (x,y) uv coordinate
    // 0.0 is black, 1.0 is white
    // r is the radius of the circle
    float c = smoothstep(r, r-blur, d);

    return c;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from -0.5 to 0.5)
    // + correct aspect ratio
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    float pixelWidth = 2.0 / iResolution.y;

	// per (x,y) uv coordinate, find the number for
    // a circle, from 0 to 1
    // the second argument is the radius from 0 to 1
    // the third argument is the border blur
    float bw = Circle(uv, .3, pixelWidth);

    // create an rgb color from the b/w "color"
    vec3 rgb = vec3(bw);

    // return the rgb color and the alpha value
    // for some reason there is no difference when changing the alpha
    fragColor = vec4(rgb, 1.0);
}
