#define PI 3.1415926535

// in radians
vec2 rot2d(vec2 st, float angle){
st =  mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle)) * st;
    return st;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


	uv = rot2d(uv,PI*.1*iTime);

	float width = 0.01;
    uv=uv*2.;
    vec3 color=vec3(0);
    for(float i = 0.0; i < 20.0; i++) {
		uv.y+= (0.04 * sin(uv.x + i/20. + iTime));
        width = abs(1.0 / (250.0 * uv.y));
		color += vec3(width * uv.x, width * 0.4, width*uv.y*0.8);
	}


	fragColor = vec4(color, 1.0);
}
