vec4 B(vec2 U) {
	return texture(iChannel0,U/R);
}
void mainImage( out vec4 Q, in vec2 U )
{ R = iResolution.xy;

   Q = B(U);
 	vec4 
        n = B(U+vec2(0,1)),
        e = B(U-vec2(0,1)),
        s = B(U+vec2(1,0)),
        w = B(U-vec2(1,0));

 	vec3 no = normalize(vec3(e.z-w.z,n.z-s.z,.02));
 	float h = dot(no,normalize(vec3(1,3,2)));
 	if (abs(Q.z)<3.)Q = 0.5+0.5*h-vec4(.7,.5,.3,1)*length(Q.xyz-vec3(0,0,-.17));
 	else Q = texture(iChannel1,U/R);
}

void mainImage( out vec4 Q, in vec2 U )
{	R = iResolution.xy;
    vec3 o = d3(U/R);
    Q = texture(iChannel0,U/R);
    vec4
        u = s3d(iChannel0,o,+vec3(0,0,1)),
        d = s3d(iChannel0,o,-vec3(0,0,1)),
        l = s3d(iChannel0,o,+vec3(1,0,0)),
        r = s3d(iChannel0,o,-vec3(1,0,0)),
        f = s3d(iChannel0,o,+vec3(0,1,0)),
        b = s3d(iChannel0,o,-vec3(0,1,0)),
        dx = 1./6.*(u+d+l+r+f+b)-Q;
   	Q.xy += dx.xy*vec2(1,.3);
    float x = Q.x*Q.y*(1.-Q.y);
    Q.y = Q.y+x-0.035;
    Q.x = Q.x+0.1*Q.x*(1.-Q.x)-x;
 	Q = max(Q,0.);
    if (iFrame < 1){
        Q = vec4(1,0,0,0);
        if (length(o-.5)<.1) Q.y=1.;
    }
}

#define N 6.
vec2 R;
vec2 d2 (vec3 U) {
	float i = floor(U.z*N*N);
	return U.xy/N+vec2(mod(i,N),floor(i/N))/N;
}
vec3 d3 (vec2 U) {
    vec2 o = floor(U*N)/N;
	return vec3(fract(U*N),o.x/N+o.y);
}
vec4 s3d (sampler2D T,vec3 U,vec3 r) {
    U.z += r.z/N/N;
    U.xy += r.xy/R*N;
    //if (U.x<0.||U.x>=1.||U.y<0.||U.y>=1.||U.z<0.||U.z>=1.)
    //    return vec4(0);
    return texture(T,d2(U));
}
vec4 s3d (sampler2D T,vec3 U) {
    return texture(T,d2(U));
}

vec4 A(vec2 U) {
	return texture(iChannel0,U/R);
}
mat2 ro (float a) {
	float s = sin(a),c = cos(a);
    return mat2(c,-s,s,c);
}
void mainImage( out vec4 Q, in vec2 U )
{ R = iResolution.xy;

   	vec3 p = vec3(0,0,-1.7);
 	vec3 d = normalize(vec3(2.*(U-0.5*R)/R.y,2));
 	float u = 0.2*iTime;
 	float o = 0.;
    if (iMouse.z>0.){
        u = 6.2*iMouse.x/R.x;
        o = 6.2*iMouse.y/R.y;
    }
 	p.xz *= ro(u);
 	d.xz *= ro(u);
 	p.xy *= ro(o);
 	d.xy *= ro(o);
 	Q = vec4(0);
 	vec4 s;
 	for (int i = 0; i < 150; i++) {
        vec3 o = abs(p)-.6;
        float x = max(o.x,max(o.y,o.z));
        if (x < 1e-2) {
        	s = s3d(iChannel0,p*0.5+0.5);
            p += d*.03*(1.-exp(-(s.x)*(1.-s.y)));
        } else p += x*d;
 	}
 	Q = vec4(p,1);

}
