void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;    
    uv.x+=uv.y*0.2;
    float t = iTime * 0.2;
    t*= uv.y > fract(uv.x*2.0)? 1.0: -1.0;
    uv.x += t;
    float y = uv.y;
    float e = uv.x*40.;
    float d = tan(e)*0.1+0.4+sin(uv.x*5.5)*0.1;
    float k = smoothstep(abs(y+0.5)*0.5,y*0.3,d);
    fragColor = vec4(k);
}
