#define PI 3.14159265

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    float x0 = uv.x * 2. * PI;
    float p0 = uv.y * 2. * PI;

    float k = sin(iTime * 0.2) + 1.0;

    float p = p0;
    float x = x0;

    for (int i = 0; i < 50; ++i) {
    	p = mod(p + k * sin(x), 2. * PI);
    	x = mod(x + p, 2. * PI);
    }

    float q = abs(p - p0);

    vec3 col = vec3(
        sin(q), sin(q + PI / 3.), sin(q + 2. * (PI / 3.))
    );

    fragColor = vec4(col, 1.);
}
