void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord/iResolution.xy;

    vec3 col = vec3(0.0, 0.1875, 0.5078125);

    vec2 q = p - vec2(0.5, 0.5);

    float r = 0.2 + 0.2*cos(atan(q.x, q.y) * 5.0 + q.x*150.0 + iTime * 4.0);
    float v = length(q) - r;
    col += smoothstep(-1.,1., v/fwidth(v));

    r = 0.1;
    col +=  smoothstep(r, r + 0.5, abs(q.x));

    // Output to screen
    fragColor = vec4(col, 1.0);
}
