void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - vec2(0.5,0.5);

	vec4 col = vec4(0);
    float thickness = 5.0/iResolution.y;

    for(float k=0.0;k<1.0;k+=0.05){
    	if(abs(uv.y - (k-0.5)*sin(uv.x*10.0 + 10.0*(k-0.5)*iTime))<thickness*(k+0.1))
        	col=vec4(1.0,cos(3.14*(1.0-k)/2.0),cos(k*3.14/2.0),1.0);
    }

    // Output to screen
    fragColor = col;
}
