void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = vec4(1.0) - vec4(vec3(texture(iChannel0, uv).x), 0.0);
}

float isKeyToggled(int keyCode) {
    return texelFetch( iChannel1, ivec2(KEY_F,2), 0 ).x; // Toggled information is on layer 2.
}

const float STROKE_THICKNESS = 0.01;

float stroke( vec2 a, vec2 b, vec2 p ) {
	vec2 pa = p - a, ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h ) - STROKE_THICKNESS;
}

float isKeyPress(int keyCode) {
    return texelFetch( iChannel1, ivec2(keyCode,1), 0 ).x; // Press information is on layer 1;
}

// Same as <keyCode is pressed> ? a : b
vec4 ifKeyPress(int keyCode, vec4 a, vec4 b) {
    return mix(b, a, isKeyPress(keyCode));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xx;
    vec4 m = iMouse / iResolution.xxxx;
    vec2 currentMousePosition = m.xy;
    vec2 mouseDownPosition = m.zw;
    float isLMBPressed = float(m.z > 0.); // When LMB is not pressed m.zw is -1.0 * lastMouseDownPosition
    float isF = isKeyToggled(KEY_F);

    vec4 lastFrame = texture(iChannel0, fragCoord/iResolution.xy);
    vec2 previousMousePosition = lastFrame.zw;
    float wasLMBPressed = lastFrame.y;

    vec2 strokeStart = mix(currentMousePosition, previousMousePosition, wasLMBPressed);

    float col = mix(1., 
                    mix(stroke(strokeStart, currentMousePosition, uv),
                        stroke(currentMousePosition, mouseDownPosition, uv),
                        isF),
                    isLMBPressed);

    float value = 1.0 - smoothstep(0.0, 0.0001, col);
    value = max(lastFrame.x, value);

    // x  -> 0.0 (blank) or 1.0 (painted)
    // y  -> 0.0 (LMB pressed) or 1.0 (LMB not pressed)
    // zw -> vec2 (current mouse position)
    // R resets canvas
    fragColor = ifKeyPress(KEY_R,
                           vec4(0.0, isLMBPressed, currentMousePosition),
                           vec4(value, isLMBPressed, currentMousePosition));
}

const int KEY_LEFT  = 37;
const int KEY_UP    = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN  = 40;
