float hash(vec2 p){
	p = fract(p*vec2(1253.4,567.8));	
    p+= dot(p,p+91.011);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord/iResolution.x;
    uv+=iTime*0.1;
    uv*=mat2(.7,-.7,.7,.7)*20.;

    vec2 id = floor(uv);
	uv = fract(uv)-0.5;


    float rnd = hash(id);
    if(rnd <0.5){
    	uv.x*=-1.;
    }
    float thickness = 0.25;
    float blur= 20./iResolution.x;

    vec3 col = min(smoothstep(thickness-blur,thickness+blur,abs(vec3(uv.x+uv.y)-0.5)),
                   smoothstep(thickness-blur,thickness+blur,abs(vec3(uv.x+uv.y)+0.5)))*vec3(1,.84,0.);


    fragColor = vec4((col),1.0);
}
