void mainImage(out vec4 O, in vec2 C)
{
	O-=O;
    O+=.05;
    for(float i,j=0.;j<12.;j++)
    for(i=0.;i<150.;i++)
        O+=abs(cos(vec4(6,3,5,0)+i*5.)+.2-.3)*
        exp(-40.*length(cross((abs(fract(fract(37.*sin((vec3(6,5,9)+i*.3)))+
        (iTime+j*.07)*.1)*2.-1.)*2.-1.)*3.,
        vec3(C.xy/iResolution.y,1))));
}
