void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0 -iResolution.xy) / iResolution.y;

    float s = sin(uv.x * 2.0 + iTime) *0.1 -0.7;
    float landMask1 = 1.0 - step(s, uv.y);

    float s2 = sin(uv.x * 2.0 + iTime + 2.5) *0.1 -0.35;
    float landMask2 = 1.0 - step(s2, uv.y);


    float s3 = sin(uv.x * 1.75 + iTime) *0.1 - 0.1;
    float landMask3 = 1.0 - step(s3, uv.y);

    vec4 outColor = vec4(0);
    outColor = mix(outColor, vec4(0.2,0.4,0.3,0)*0.5, landMask3);
    outColor = mix(outColor, vec4(0.2,0.4,0.3,0)*0.75, landMask2);
    outColor = mix(outColor, vec4(0.2,0.4,0.3,0), landMask1);

    float dist = distance(uv, vec2(0.95,0.5));
    float moonBloomMask = clamp((1.0-dist*2.5), 0.0, 1.0);
    float moonMask =step(dist, 0.15);

    outColor = mix(outColor, vec4(0.75,0.75,1.0, 0.0), moonBloomMask);
    outColor = mix(outColor, vec4(1.0,1.0,1.0, 0.0), moonMask);

    // Output to screen
    fragColor = outColor;
}
