//Band function
float Band(float t, float start, float end, float blur) {
 	float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);
    float finalStep = step1*step2;
    return finalStep;
}

//Rectangle function
float Rectangle(vec2 uv, float blur, float left, float right, float bottom, float top) {
    float rect;
    rect = Band (uv.x, left, right, blur);
    rect = rect*Band (uv.y, bottom, top, blur);
    return rect;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord/iResolution.xy;
    uv = uv-0.5;
    uv.x = uv.x*(iResolution.x/iResolution.y);

    vec4 col, col1, col2;   
	float c;
    float x = uv.x;
    float y = uv.y;

    //Line
    c = Rectangle(vec2(x, y), 0.001, -1.0, 1.0, -0.005, 0.005);
    c += Rectangle(vec2(x, y), 0.001, -0.005, 0.005, -1.0, 1.0);
    col1 = vec4(1.0, 0.0, 0.0, 0.0)*c;

    //Sinus Wave
    float m = (0.5-x)*(x+0.5);
    float a = 1.0, f=10.0, t=1.5;
    m = m*sin(iTime*t-x*f)*a;
    y = y+m;

    //The Wave Line
    c += Rectangle(vec2(x, y), 0.001, -1.0, 1.0, -0.005, 0.005);

    col2 = vec4(1.0, 1.0, 2.0, 3.0)*c;
    col = mix(col1, col2, 0.3);

    //Output
    fragColor = vec4(col);
}
