// 185 (FabriceNeyret2)
void mainImage(out vec4 o, vec2 f)
{
	vec2 R = iResolution.xy,
         u = f+R, v = iTime*.1+u*.5;
    u = abs( u* mat2(cos( iTime*.1 + sin(v).x*cos(v).y + vec4(0,33,11,0) ) )) / R;
    o = cos( vec4(0,1,2,0) + step(.0, u = min(u, u.y) - .1 ).x /.1 *u.x );
}

