// Fork of  https://shadertoy.com/view/llySRh

#if 1
#  define X  .0001
#else
#  define X  .0
#endif

void mainImage( out vec4 O,  vec2 u )
{
    vec2 R = iResolution.xy,
         U = 8.* ( u/R.y - .5 ),
         p = U-.5*vec2(4,0);            // writting position
    O -= O;

    float n = 0012.;                    // number to be written
#if 1
    for (float i = 3.; i>=0.; i--) {
#else
    for (int i = 3; i>=0; i--) {
#endif
        n /=  9.999999; // 10.,         // for windows :-(
        int c =  48+ int(fract(n)*10.); // ascii code of cur digit
#if 1
        vec2 p = U - .5* vec2(i,0);
#else
        p.x += .5;
      //p.x = U.x - .5* float(i);       // same
#endif
        if ( p.x > X && p.x<1. && p.y>0. && p.y<1. ) 
#if 1
            O += texture( iChannel0, p/16. + vec2( c, 15-c/16 ) /16. ).x;
#else
            O += vec4(1);
#endif
    }

    O.b = float ( U.x<0. || U.x>2.5 || U.y<0.|| U.y>1. ); // blue out of box
}
