float jifen(float x, float y){
	return log(x + sqrt(x*x + y*y));
}

float result(float l, float x, float y, float d){
	float b1 = jifen(l - x, y - d) - jifen(-l - x, y - d);
    float b2 = jifen(l - x, y + d) - jifen(-l - x, y + d);
    return b1 - b2;
}

vec2 fanyan(vec2 o, float r, vec2 p){
    vec2 op = vec2(p.x - o.x, p.y - o.y);
    vec2 f = vec2(0.0, 0.0);
    float k = r * r / (op.x * op.x + op.y * op.y);
    f.x = o.x + op.x * k;
    f.y = o.y + op.y * k;
    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);


    float x = uv.x - 0.5;
    float y = uv.y - 0.5;
    float r = 0.2;

    vec2 o = vec2(0, r);
    float l = r * 0.5;
    float y0 = r * 0.5;

    // 画反演圆
    if(abs(length(vec2(x, y) - o) - r) < 0.005){
    	fragColor.b = 1.0;
    }
    // 画磁铁所在的圆
    if(abs(length(vec2(x, y)) - r) < 0.005){
    	fragColor.b = 1.0;
    }

    vec2 p = fanyan(o, r, vec2(x, y)); // 求当前坐标的反演点
    float c = 0.0;
    c = result(l, p.x, p.y - y0, 0.0001); // 在反演之后的空间计算磁场
    fragColor.r = mod(abs(c)*100.0, 1.0);

    // 测试用
    // if(abs(p.y - y0) < 0.01){
    //     fragColor.r = 1.0;
    // }

    // if(abs(p.y) > 0.1){
    //     fragColor.r = 1.0;
    // }

    // 画网格
    if(abs(mod(y, 0.1)) < 0.002 || abs(mod(x, 0.1)) < 0.002){
    	fragColor.g = 0.5;
    }
}
