vec3 brown = vec3(0.3,0.2,0.2);
vec3 black = vec3(0.0,0.0,0.0);
vec3 red = vec3(0.7,0.0,0.0);
vec3 blue = vec3(0.5,0.5,.5);
vec3 yellow = vec3(0.5,0.5,0.0);
vec3 green = vec3(0.1,0.9,0.0);
vec3 white = vec3(1.0,1.0,1.0);

float circle(vec2 uv, vec2 pos, float r)
{       
    uv.x *= iResolution.x/iResolution.y;

    float dist = distance(pos, uv);
    float value = step(r, dist);

    return value;
}

float land(vec2 uv, vec2 pos, float r)
{       
    uv.x *= 0.4;

    float dist = distance(pos, uv);
    float value = smoothstep(r, r + 0.03, dist);

    return value;
}

float windMill(vec2 uv, vec2 pos, float r)
{   
    float dist = distance(pos, uv) * r;
    float value = step(r, dist);

    uv -= pos;

    float speed = 2.0;

    float angle = (atan(uv.y, uv.x) + iTime * speed);

    float radius = cos(angle * 6.0);

    value = step(radius , dist);

    return value;
}

float leaf(vec2 uv, vec2 pos, float r)
{   
    float dist = distance(pos, uv) * r;
    float value = step(r, dist);

    uv -= pos;

    float speed = 4.0;

    float angle = (atan(uv.y, uv.x) + iTime * speed);

    float radius = cos(angle * 2.0);

    value = step(radius , dist);

    return value;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float mLand = land(uv, vec2(0.2,-0.2), 0.4);
    vec3 landCol = mix(brown, sin(yellow * iTime * 2.0) / 1.5 + 0.3, mLand);

    float height = step(0.07,uv.y) - step(0.3,uv.y);
    float width = step(0.1,uv.x) -step(0.12,uv.x);

    vec3 rectangle = mix(landCol, red, height * width);

    height = step(0.1,uv.y) - step(0.35,uv.y);
    width = step(0.4,uv.x) - step(0.42,uv.x);

    rectangle = mix(rectangle, red, height * width);

    height = step(0.07,uv.y) - step(0.3,uv.y);
    width = step(0.8,uv.x) -step(0.82,uv.x);

    rectangle = mix(rectangle, red, height * width);

    float mWindMill = windMill(uv, vec2(0.11,0.3), 6.0);
    vec3 millCol = mix(green, rectangle, mWindMill);

    float mWindMill2 = windMill(uv, vec2(0.41,0.35), 6.0);
    millCol = mix(green, millCol, mWindMill2);

    float mWindMill3 = windMill(uv, vec2(0.81,0.3), 6.0);
    millCol = mix(green, millCol, mWindMill3);

    float mLeaf = leaf(uv, vec2(clamp(sin(0.5 * iTime) + 0.5, -0.1, 2.0),0.8), 10.0);
    millCol = mix(green, millCol, mLeaf);





    // Output to screen
    fragColor = vec4(millCol,1.0);
}
