float jifen(float x, float y){
	return log(x + sqrt(x*x + y*y));
}

float result(float l, float x, float y, float d){
	float b1 = jifen(l - x, y - d) - jifen(-l - x, y - d);
    float b2 = jifen(l - x, y + d) - jifen(-l - x, y + d);
    return b1 - b2;
}

vec2 fanyan(vec2 o, float r, vec2 p){
    vec2 op = vec2(p.x - o.x, p.y - o.y);
    vec2 f = vec2(0.0, 0.0);
    float k = r * r / (op.x * op.x + op.y * op.y);
    f.x = o.x + op.x * k;
    f.y = o.y + op.y * k;
    return f;
}

float halfCircle(float x, float y, float r, float d){
	vec2 o = vec2(0, r);
    float l = r * 0.5;
    float y0 = r * 0.5;
    vec2 p = fanyan(o, r, vec2(x, y)); // 求当前坐标的反演点
    float c = result(l, p.x, p.y - y0, d); // 在反演之后的空间计算磁场
    return c;
}

float line(float x, float y, float l, float ox, float oy, float d){
    return result(l, x + ox, y + oy, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    float x = uv.x - 0.5;
    float y = uv.y - 0.5;
    float r = 0.1;
    float d = 0.0001;
    float l = 0.1;

    float c0 = halfCircle(x, y, r, d);
    float c1 = line(y, x, l, -l, r, d);
    float c2 = line(y, x, l, -l, -r, d);
    float c = c0 - c1 + c2;
    fragColor.r = mod(abs(c) * 500.0, 1.0);

}
