// fbm thanks to iq!
#define NUM_OCTAVES 6

float rand(float n)
{
    return fract(sin(n) * 43758.5453123);
}

float rand(vec2 n) 
{ 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(float p)
{
	float fl = floor(p);
  	float fc = fract(p);
	return mix(rand(fl), rand(fl + 1.0), fc);
}

float noise(vec2 p)
{
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

float fbm_2(vec2 x) 
{
	float v = 0.0;
	float a = 0.5;
	vec2 shift = vec2(100);
    mat2 rot = mat2(cos(0.5), sin(0.5), -sin(0.5), cos(0.50));
	for (int i = 0; i < NUM_OCTAVES; ++i) 
    {
		v += a * noise(x);
		x = rot * x * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}

float sb(float t)
{
    return sin(t) * 0.5 + 0.5;
}

float cb(float t)
{
	return cos(t) * 0.5 + 0.5; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 aspect = 1.0 / (iResolution.xx / iResolution.xy); 

    vec2 ndc = uv * 2.0 - 1.0;

    uv *= aspect;

    vec2 c1 = vec2(1.0, 1.0) + vec2(sb(iTime * 0.3) * 0.2, cb(iTime * 0.11) * 0.2);
    vec2 c2 = vec2(-1.0, -1.0) - vec2(cb(iTime * 0.5) * 0.2, sb(iTime * 2.0) * 0.2);

    float s1 = 1.0 + cb(iTime * 0.1) * 0.8;
    float s2 = 1.0 + sb(iTime * 0.1) * 0.8;

    vec3 p1 = vec3(255.0, 87.0, 51.0) / 255.0;
    vec3 p2 = vec3(87.0, 24.0, 69.0) / 255.0;
    vec3 p3 = vec3(199.0, 0.0, 57.0) / 255.0;

    float nt = iTime * 0.4;
    vec2 nuv = uv * 5.0;

    float n = fbm_2(nuv + nt) + fbm_2(nuv - nt * vec2(1.0, 0.0));
	float n2 = fbm_2(nuv - cos(nt));
	float n3 = fbm_2(nuv + vec2(0.0, 1.0) * cos(nt));

    float r2 = max(1.0 - length(c1 - ndc) * s1, 0.0);
    float r3 = max(1.0 - length(c2 - ndc) * s2, 0.0);
    float rr = 0.1 + (r2*r2 + r3*r3);

    vec3 col = p1 * n + p2 * n2 + p3 * n3;

    fragColor = vec4(col * rr, 1.0);
}
