void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 p1 = iResolution.xy/2.;
  //vec2 p2 = iMouse.xy;
    vec2 p2 = vec2(iResolution.x * (sin(iTime*0.2) * .5 + .5), 0);

  float d = abs((p2.y - p1.y) * fragCoord.x - (p2.x - p1.x) * fragCoord.y + p2.x * p1.y - p2.y * p1.x);
  d /= sqrt(pow(p2.y - p1.y, 2.) + pow(p2.x - p1.x, 2.));

  fragColor = vec4(clamp(d, 0., 1.2)/1.2);
}
