vec3 green = vec3(0,1.,0.);
vec3 yellow = vec3(1.,1.,0.);
vec3 black = vec3(0.,0.,0.);
vec3 gray = vec3(.5,.5,.3);

float circle(vec2 uv,float distanceAa, float distanceAb,float ditanceBa,float distanceBb,float size)
{

    float val = distance(uv, vec2(distanceAa, distanceAb)) * distance(uv, vec2(ditanceBa, distanceBb));

	val = step(val, size);

    return val;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;

    vec2 shapePos= vec2(.5*ratio,.5);
    float dist = distance(shapePos,uv)*2.;

    uv -=shapePos;
    float angle= atan(uv.y,uv.x);
    float radius = cos(.2*angle);
    radius = abs(sin(circle(uv,.5,.5,.5,.5,5.)+10.+iTime)*sin(angle*10.+iTime))*2.;

    float value = 1.-step(radius,dist);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 val = mix(col,green,value);

    // Output to screen
    fragColor = vec4(val,uv);
}
