vec2 P;

#define F    for (int k=0; k<9; k++ ) if(k!=4) P=vec2(mod(float(k), 3.),float(k/3))-1., 

float   N(vec2 u)
{
    float   r = .0;
	F
        r += A(u+P/R).x /8.;
    return r;
}

void mainImage(out vec4 o, in vec2 u)
{
    vec2    v = (u - R*.5) / R.y;
    o -= o;
    o += (( // big erratic negative ball
        (length(v-(R.y/R.x)*1.*vec2(cos(iTime*1.5*.125+sin(iTime*.125))*.75,1.*cos(iTime*.5)*sin(iTime*.375)))*10. )<1.5
        ||
        ( // negative mouse square
        mylength((u-iMouse.xy)/R.y)<.1 && iMouse.z>.5)
        )
          ? -1. : 0.)
        +
        ( // positive circling ball
        (length(v-.5*vec2(cos(-iTime), sin(-iTime)))<.05)? 1. : 0.
        )
        ;
    u/=R;
    vec3 r;r-=r;
    // Neighborhood collection into r
    F r.y = max(r.y, N((u+P/R) ) /8. );
    F r.x = max(r.x, A((u+P/R)).x /8. );
    r.z = min(N(u), A(u).x );
    o.x +=
        clamp(
            A(u).x
            +
           (4./(r.y+5.0)+12.*r.y+r.x*4.-r.z*3.)/(r.z/.01+r.y/.1-.7199)/32.
        , 0., 1.);
}
