float random(vec2 uv){
	return fract(sin(dot(uv, vec2(-0.100,0.050))) * 43759.737);
}


vec2 transform_uv(vec2 fuv, float disturb){
    if(disturb > 0.8){
    	fuv = 1. - fuv;
    }else if(disturb > 0.4){
    	fuv = vec2(1.0 - fuv.x, fuv.y);
    }else if(disturb > 0.1){
    	fuv = vec2(fuv.x, 1.0 - fuv.y);
    }
    return fuv;
}

float make_circle(vec2 dir){
	return smoothstep(0.39, 0.41, length(dir)) - smoothstep(0.59, 0.61, length(dir));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 10.*(2. * fragCoord - iResolution.xy) / iResolution.y * abs(sin(iTime*0.4)+1.5);
    uv.x -= 3.0 * iTime;

    vec2 iuv = floor(uv);
    vec2 fuv = fract(uv);
    float disturb = random(iuv);

    vec2 trans_uv = transform_uv(fuv, disturb);
	vec3 col_line = vec3(0.);
    vec3 col_circle = vec3(0.);

    //line maze
    float c = smoothstep(0.19, 0.2, abs(trans_uv.x - trans_uv.y));
    col_line = vec3(c);
    //circle maze                           
    c = make_circle(trans_uv) + make_circle(trans_uv - 1.);
   	col_circle = vec3(c);

    float sep = iResolution.x * (0.555 * cos(0.18*3.1415*iTime + 3.1415) + 0.5); 
    vec3 finalcol = mix(col_line, col_circle, step(sep, fragCoord.x));


    fragColor = vec4(finalcol,1.0);
}
